using Xunit;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using Moq;
using Server.DataModels;
using Server.Services;

public class BookServiceTests{
    [Fact]
    public async Task GetBooksAsync_ReturnsBooks(){
        // Arrange
        var mockBookService = new Mock<IbookService>();
        mockBookService.Setup(service => service.GetAllBooksAsync())
            .ReturnsAsync(new List<Book>{
                new Book{
                    BookId  = 1, 
                    BookTitle = "Storm Breaker",
                    BookImage = "data:image/jpeg;base64,",
                    BookPrice = 10
                    },
                new Book{
                    BookId  = 2, 
                    BookTitle = "Point Blanc",
                    BookImage = "data:image/jpeg;base64,",
                    BookPrice = 10
                    },
            });

        // Act
        var result = await mockBookService.Object.GetAllBooksAsync();

        // Assert
        Assert.Equal(2, result.Count());
        Assert.Equal("Storm Breaker", result.First().BookTitle);
    }
}